public class UnstableIce extends Ice
{
	public UnstableIce(int maxChar)
	{
		super(maxChar, 0);
		System.out.println("--> UnstableIce(2)"); //ezt m√©g ki kell talalni, egyelore beirok kettot, mert talan az fog kelleni a tesztekne
		System.out.println("<--");
	}

	@Override
	public void moveHere(Character c) {

		System.out.println("--> moveHere(c)");
		addCharacter(c);
		c.getIce().removeCharacter(c);
		c.setIce(this);
		if(getCharNum() >= getMaxCharacters())
			for (int i = getCharNum()-1; i>= 0; i--)
			{
				try
				{
					getCharacter(i).fallInWater();
				} catch (Exception e)
				{
					System.out.println(e);
				}
				
			}
		System.out.println("<--");
	}
	
}
